import React from "react";
import { useUser } from "../UserProvider";
import jwt_decode from "jwt-decode";

const Comment = (props) => {
  const user = useUser();
  const decodedJwt = jwt_decode(user.jwt);
  const {
    id,
    createdDate,
    createdBy,
    text,
    emitDeleteComment,
    emitEditComment,
  } = props;
  return (
    <>
    <div className="comment-bubble">
      <div className="d-flex gap-5" style={{ fontWeight: "bold" }}>
        <div>
          {createdBy.name}:{"  "}
        </div>
        {decodedJwt.sub === createdBy.username ? (
          <>
            {" "}
            <div
              onClick={() => emitEditComment(id)}
              style={{ cursor: "pointer", color: "blue" }}
            >
              Edit
            </div>
            <div
              onClick={() => emitDeleteComment(id)}
              style={{ cursor: "pointer", color: "red" }}
            >
              Delete
            </div>
          </>
        ) : (
          <></>
        )}
      </div>
      <div>{text}</div>
    </div>
    <div style={{marginTop:"-1.25em",marginLeft:"1.4em",fontSize:"12px"}}>Time from x</div>
    </>
  );
};

export default Comment;
