import { useEffect } from "react";
import "./App.css";
import { useLocalState } from "./util/useLocalStorage";
import { Routes, Route } from "react-router-dom"; // Make sure to import Routes and Route
import Login from "./Login";

function App() {
  // This will log "Hello" to the console when the component renders
  const [jwt, setJwt] = useLocalState("", "jwt");

  useEffect(() => {
    if (!jwt) {
      const reqBody = {
        username: "vivek",
        password: "asdfasdf",
      };

      fetch("api/auth/login", {
        headers: {
          "Content-Type": "application/json",
        },
        method: "post",
        body: JSON.stringify(reqBody), // Sending the request body as JSON
      })
        .then((response) => Promise.all([response.json(), response.headers]))
        .then(([body, headers]) => {
          setJwt(headers.get("authorization")); // Getting the "authorization" header value
        });
    }
  }, []); // The empty array means this effect runs only once when the component mounts

  useEffect(() => {
    console.log(`JWT:${jwt}`);
  }, [jwt]);

  return (
    <Routes>
      <Route path="login" element={<Login />} />
    </Routes>
  );
}

export default App;
