import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useLocalState } from "../util/useLocalStorage";
import ajax from "../Services/fetchService";
import { Badge, Button, Col, Container, Form, Row } from "react-bootstrap";

const AssignmentView = () => {
  const { id } = useParams();
  const [assignment, setAssignment] = useState({
    branch: "",
    githuburl: "",
  });
  const [jwt, setJwt] = useLocalState("", "jwt");

  function updateAssignment(prop, value) {
    const newAssignment = { ...assignment };
    newAssignment[prop] = value;
    setAssignment(newAssignment);
    //  console.log(assignment)
  }

  function save() {
    ajax(`/api/assignments/${id}`, "PUT", jwt, assignment).then(
      (assignmentsData) => {
        setAssignment(assignmentsData);
      }
    );
  }

  useEffect(() => {
    ajax(`/api/assignments/${id}`, "GET", jwt).then((assignmentsData) => {
      if (assignmentsData.branch === null) assignmentsData.branch = "";
      if (assignmentsData.githuburl === null) assignmentsData.githuburl = "";
      setAssignment(assignmentsData);
    });
  }, []);

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h1>Assignment {id}</h1>{" "}
        </Col>
        <Col>
          <Badge pill bg="info" style={{fontSize:"1em"}}>
          {assignment.status}
          </Badge>{" "}
        </Col>
      </Row>

      {assignment ? (
        <>
          <Form.Group as={Row} className="my-3" controlId="formPlaintextEmail">
            <Form.Label column sm="3" md="2">
              Github URL:
            </Form.Label>
            <Col sm="9" md="8" lg="6">
              <Form.Control
                id="githuburl"
                onChange={(e) => updateAssignment("githuburl", e.target.value)}
                type="url"
                placeholder="http://github.com/username/repo-name"
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
            <Form.Label column sm="3" md="2">
              Branch :
            </Form.Label>
            <Col sm="9" md="8" lg="6">
              <Form.Control
                id="branch"
                onChange={(e) => updateAssignment("branch", e.target.value)}
                type="text"
                placeholder="example_branch_name"
              />
            </Col>
          </Form.Group>

          <Button onClick={() => save()}>Submit Assignment</Button>
        </>
      ) : (
        <></>
      )}
    </Container>
  );
};

export default AssignmentView;
