import React from "react";

const Comment = (props) => {
  const { createdDate, createdBy, text } = props;
  return (
    <div className="comment-bubble">
      <div className="d-flex" style={{ fontWeight: "bold" }}>
        {createdBy.name}:{"  "}
        <span 
        className="pl-4"
        style={{cursor:"pointer",colour:"blue"}}>Edit</span>
      </div>
      <div>{text}</div>
    </div>
  );
};

export default Comment;
