import React, { useState } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [selectedRoles, setSelectedRoles] = useState([]);
  const navigate = useNavigate();

  const availableRoles = ["ROLE_STUDENT", "ROLE_CODE_REVIEWER"];

  function handleRoleSelection(role) {
    if (selectedRoles.includes(role)) {
      setSelectedRoles(selectedRoles.filter((r) => r !== role));
    } else {
      setSelectedRoles([...selectedRoles, role]);
    }
  }

  function sendSignupRequest() {
    const reqBody = {
      username: username,
      password: password,
      roles: selectedRoles,
    };

    fetch("api/auth/signup", {
      headers: {
        "Content-Type": "application/json",
      },
      method: "POST",
      body: JSON.stringify(reqBody),
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Signup successful! You can now log in.");
          navigate("/login"); // Redirect to the login page
        } else {
          return Promise.reject("Signup failed");
        }
      })
      .catch((message) => {
        alert(message);
      });
  }

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col md="8" lg="6">
          <Form.Group className="mb-3" controlId="signupUsername">
            <Form.Label className="fs-4">Username</Form.Label>
            <Form.Control
              type="email"
              size="lg"
              placeholder="joe@gmail.com"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
        </Col>
      </Row>

      <Row className="justify-content-center">
        <Col md="8" lg="6">
          <Form.Group className="mb-3" controlId="signupPassword">
            <Form.Label className="fs-4">Password</Form.Label>
            <Form.Control
              type="password"
              size="lg"
              placeholder="Choose a password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
        </Col>
      </Row>

      <Row className="justify-content-center">
        <Col md="8" lg="6">
          <Form.Group controlId="signupRoles">
            <Form.Label className="fs-4">Select Roles</Form.Label>
            {availableRoles.map((role) => (
              <Form.Check
                key={role}
                type="checkbox"
                label={role}
                checked={selectedRoles.includes(role)}
                onChange={() => handleRoleSelection(role)}
              />
            ))}
          </Form.Group>
        </Col>
      </Row>

      <Row className="justify-content-center">
        <Col
          md="8"
          lg="6"
          className="mt-2 d-flex flex-column gap-5 flex-md-row justify-content-md-between"
        >
          <Button
            id="signupSubmit"
            type="button"
            size="lg"
            onClick={() => sendSignupRequest()}
          >
            Signup
          </Button>
          <Button
            variant="secondary"
            type="button"
            size="lg"
            onClick={() => {
              navigate("/");
            }}
          >
            Cancel
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default Signup;
